import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './QuizPage.css'; // Assuming the CSS will be stored in this file

const QuizPage = ({ getQuizDataById }) => {
  const { quizId } = useParams();
  const navigate = useNavigate();
  const [quiz, setQuiz] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);

  useEffect(() => {
    const quizData = getQuizDataById(quizId);
    if (quizData) {
      setQuiz(quizData);
    } else {
      alert('Quiz not found');
      navigate('/');
    }
  }, [quizId, getQuizDataById, navigate]);

  const handleNextQuestion = useCallback(() => {
    if (currentQuestionIndex < quiz.questions.length - 1) {
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
      setSelectedOption(null);
    } else {
      alert('Quiz completed!');
      navigate('/');
    }
  }, [currentQuestionIndex, quiz?.questions.length, navigate]);

  useEffect(() => {
    if (quiz && quiz.questions[currentQuestionIndex].timer !== 'OFF') {
      setTimeLeft(parseInt(quiz.questions[currentQuestionIndex].timer, 10));

      const timerInterval = setInterval(() => {
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(timerInterval);
            handleNextQuestion();
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);

      return () => clearInterval(timerInterval);
    } else {
      setTimeLeft(null);
    }
  }, [quiz, currentQuestionIndex, handleNextQuestion]);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  if (!quiz) {
    return <div>Loading quiz...</div>;
  }

  const currentQuestion = quiz.questions[currentQuestionIndex];

  return (
    <div className="quiz-page">
      <div className="quiz-header">
        <div className="question-count">
          {currentQuestionIndex + 1}/{quiz.questions.length}
        </div>
        {timeLeft !== null && (
          <div className="timer">
            {timeLeft < 10 ? `00:0${timeLeft}s` : `00:${timeLeft}s`}
          </div>
        )}
      </div>
      <div className="question-text">
        {currentQuestion.questionText}
      </div>
      <div className="options-grid">
        {currentQuestion.options.map((option, index) => (
          <button
            key={index}
            className={`option-button ${selectedOption === option ? 'selected' : ''}`}
            onClick={() => handleOptionSelect(option)}
          >
            {option.text}
          </button>
        ))}
      </div>
      <button
        className="next-button"
        onClick={handleNextQuestion}
      >
        {currentQuestionIndex < quiz.questions.length - 1 ? 'Next' : 'Submit'}
      </button>
    </div>
  );
};

export default QuizPage;
