function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(() => {
    // Initialize from localStorage if available
    return localStorage.getItem('isAuthenticated') === 'true';
  });

  const [userCredentials, setUserCredentials] = useState(() => {
    const savedCredentials = localStorage.getItem('userCredentials');
    return savedCredentials ? JSON.parse(savedCredentials) : null;
  });

  const [quizzes, setQuizzes] = useState(() => {
    const savedQuizzes = localStorage.getItem('quizzes');
    return savedQuizzes ? JSON.parse(savedQuizzes) : [];
  });

  useEffect(() => {
    localStorage.setItem('isAuthenticated', isAuthenticated);
  }, [isAuthenticated]);

  const handleSignup = (email, password) => {
    const credentials = { email, password };
    setUserCredentials(credentials);
    localStorage.setItem('userCredentials', JSON.stringify(credentials));
    setIsAuthenticated(false); // Stay on login after signup
  };

  const handleLogin = (email, password) => {
    if (userCredentials && userCredentials.email === email && userCredentials.password === password) {
      setIsAuthenticated(true);
    } else {
      alert("Invalid email or password");
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('isAuthenticated');
  };

  const handleCreateQuiz = (newQuiz) => {
    const updatedQuizzes = [...quizzes, newQuiz];
    setQuizzes(updatedQuizzes);
    localStorage.setItem('quizzes', JSON.stringify(updatedQuizzes));
  };

  const getQuizDataById = (quizId) => {
    const savedQuizzes = JSON.parse(localStorage.getItem('quizzes')) || quizzes;

    const quizIndex = savedQuizzes.findIndex(quiz => quiz.id === quizId);
    if (quizIndex !== -1) {
      savedQuizzes[quizIndex].views = (savedQuizzes[quizIndex].views || 0) + 1; // Increment views
      localStorage.setItem('quizzes', JSON.stringify(savedQuizzes)); // Save updated quizzes back to localStorage
      return savedQuizzes[quizIndex];
    }
    return null;
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            isAuthenticated ? <Navigate to="/home/dashboard" /> : <Auth onLogin={handleLogin} onSignup={handleSignup} />
          }
        />
        <Route
          path="/home/*"
          element={
            isAuthenticated ? <HomePage onCreateQuiz={handleCreateQuiz} onLogout={handleLogout} quizzes={quizzes} /> : <Navigate to="/login" />
          }
        />
        <Route
          path="/quiz/:quizId"
          element={<QuizPage getQuizDataById={getQuizDataById} />} // Make QuizPage publicly accessible
        />
        <Route
          path="/analytics"
          element={
            isAuthenticated ? <AnalyticsPage quizzes={quizzes} /> : <Navigate to="/login" />
          }
        />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
