import React from 'react';
import './ShareQuizModal.css';

const ShareQuizModal = ({ isOpen, onClose, quizLink }) => {
  if (!isOpen) return null;

  const handleCopyToClipboard = () => {
    navigator.clipboard.writeText(quizLink).then(() => {
      alert('Link copied to clipboard!');
    });
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content share-quiz-modal">
        <button className="close-modal" onClick={onClose}>Ã—</button>
        <h2>Congrats your Quiz is Published!</h2>
        <input
          type="text"
          value={quizLink}
          readOnly
          className="quiz-link-input"
        />
        <button className="modal-button share-button" onClick={handleCopyToClipboard}>
          Share
        </button>
      </div>
    </div>
  );
};

export default ShareQuizModal;
