import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import CreateQuizPage from './CreateQuizPage'; // Component where you select to create Q&A or Poll
import PollSummary from './PollSummary'; // Component to show Poll summary (create this)
import QuizSummary from './QuizSummary'; // Component to show Quiz summary (for Q&A)

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/create-quiz" element={<CreateQuizPage />} />
        <Route path="/poll-summary" element={<PollSummary />} /> {/* Add this route */}
        <Route path="/quiz-summary" element={<QuizSummary />} /> {/* Existing Q&A route */}
        {/* Add more routes as needed */}
      </Routes>
    </Router>
  );
};

export default App;
