import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Auth from './components/Auth';
import HomePage from './components/HomePage';
import QuizPage from './components/QuizPage'; // Import the QuizPage component

function App() {
  const [isAuthenticated, setIsAuthenticated] = React.useState(false);
  const [userCredentials, setUserCredentials] = React.useState(null);
  const [quizzes, setQuizzes] = React.useState([]);

  const handleSignup = (email, password) => {
    setUserCredentials({ email, password });
    setIsAuthenticated(false); // Stay on login after signup
  };

  const handleLogin = (email, password) => {
    if (userCredentials && userCredentials.email === email && userCredentials.password === password) {
      setIsAuthenticated(true);
    } else {
      alert("Invalid email or password");
    }
  };

  const handleCreateQuiz = (newQuiz) => {
    setQuizzes([...quizzes, newQuiz]);
  };

  const getQuizDataById = (quizId) => {
    return quizzes.find(quiz => quiz.id === quizId);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            isAuthenticated ? <Navigate to="/home/dashboard" /> : <Auth onLogin={handleLogin} onSignup={handleSignup} />
          }
        />
        <Route
          path="/home/*"
          element={
            isAuthenticated ? <HomePage onCreateQuiz={handleCreateQuiz} /> : <Navigate to="/login" />
          }
        />
        <Route
          path="/quiz/:quizId"
          element={
            isAuthenticated ? (
              <QuizPage getQuizDataById={getQuizDataById} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
