import React, { useState } from 'react';
import './QAQuestionModal.css';

const QAQuestionModal = ({ isOpen, onClose, onCreateQuiz }) => {
  const [question, setQuestion] = useState('');
  const [optionType, setOptionType] = useState('Text');
  const [options, setOptions] = useState(['']);
  const [timer, setTimer] = useState('OFF');
  const [questionNumbers, setQuestionNumbers] = useState([1, 2]);

  const handleAddOption = () => {
    if (options.length < 3) {
      setOptions([...options, '']);
    }
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...options];
    newOptions[index] = value;
    setOptions(newOptions);
  };

  const handleRemoveOption = (index) => {
    const newOptions = options.filter((_, i) => i !== index);
    setOptions(newOptions);
  };

  const handleAddQuestion = () => {
    if (questionNumbers.length < 5) {
      setQuestionNumbers([...questionNumbers, questionNumbers.length + 1]);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content qa-question-modal">
        <div className="question-nav">
          {questionNumbers.map((num) => (
            <div key={num} className="question-num">
              {num}
            </div>
          ))}
          {questionNumbers.length < 5 && (
            <div className="add-question" onClick={handleAddQuestion}>
              +
            </div>
          )}
        </div>
        <input
          type="text"
          placeholder="Poll Question"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          className="poll-question-input"
        />
        <div className="option-type-container">
          <label>Option Type</label>
          <label>
            <input
              type="radio"
              name="optionType"
              value="Text"
              checked={optionType === 'Text'}
              onChange={() => setOptionType('Text')}
            />
            Text
          </label>
          <label>
            <input
              type="radio"
              name="optionType"
              value="Image URL"
              checked={optionType === 'Image URL'}
              onChange={() => setOptionType('Image URL')}
            />
            Image URL
          </label>
          <label>
            <input
              type="radio"
              name="optionType"
              value="Text & Image URL"
              checked={optionType === 'Text & Image URL'}
              onChange={() => setOptionType('Text & Image URL')}
            />
            Text & Image URL
          </label>
        </div>
        <div className="options-container">
          {options.map((option, index) => (
            <div key={index} className="option">
              <input
                type="text"
                value={option}
                onChange={(e) => handleOptionChange(index, e.target.value)}
                placeholder="Text"
              />
              {index > 0 && (
                <button className="remove-option" onClick={() => handleRemoveOption(index)}>
                  🗑️
                </button>
              )}
            </div>
          ))}
          {options.length < 4 && (
            <button className="add-option" onClick={handleAddOption}>
              Add option
            </button>
          )}
        </div>
        <div className="timer-container">
          <label>Timer</label>
          <button
            className={`timer-button ${timer === 'OFF' ? 'active' : ''}`}
            onClick={() => setTimer('OFF')}
          >
            OFF
          </button>
          <button
            className={`timer-button ${timer === '5 sec' ? 'active' : ''}`}
            onClick={() => setTimer('5 sec')}
          >
            5 sec
          </button>
          <button
            className={`timer-button ${timer === '10 sec' ? 'active' : ''}`}
            onClick={() => setTimer('10 sec')}
          >
            10 sec
          </button>
        </div>
        <div className="modal-buttons">
          <button className="modal-button cancel-button" onClick={onClose}>
            Cancel
          </button>
          <button
            className="modal-button continue-button"
            onClick={() => onCreateQuiz({ question, optionType, options, timer })}
          >
            Create Quiz
          </button>
        </div>
      </div>
    </div>
  );
};

export default QAQuestionModal;
