import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Auth from './components/Auth';
import HomePage from './components/HomePage';

function App() {
  const [isAuthenticated, setIsAuthenticated] = React.useState(false);
  const [userCredentials, setUserCredentials] = React.useState(null);

  const handleSignup = (email, password) => {
    setUserCredentials({ email, password });
    setIsAuthenticated(false); // Stay on login after signup
  };

  const handleLogin = (email, password) => {
    if (userCredentials && userCredentials.email === email && userCredentials.password === password) {
      setIsAuthenticated(true);
    } else {
      alert("Invalid email or password");
    }
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            isAuthenticated ? <Navigate to="/home" /> : <Auth onLogin={handleLogin} onSignup={handleSignup} />
          }
        />
        <Route
          path="/home"
          element={
            isAuthenticated ? <HomePage /> : <Navigate to="/login" />
          }
        />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
