import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Auth from './components/Auth';
import HomePage from './components/HomePage';
import QuizPage from './components/QuizPage'; // Import the QuizPage component

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userCredentials, setUserCredentials] = useState(null);
  const [quizzes, setQuizzes] = useState(() => {
    // Initialize quizzes from localStorage if available
    const savedQuizzes = localStorage.getItem('quizzes');
    return savedQuizzes ? JSON.parse(savedQuizzes) : [];
  });

  // Auto-login if credentials exist in localStorage
  React.useEffect(() => {
    const savedCredentials = localStorage.getItem('userCredentials');
    if (savedCredentials) {
      const { email, password } = JSON.parse(savedCredentials);
      setUserCredentials({ email, password });
      setIsAuthenticated(true); // Automatically log in the user
    }
  }, []);

  const handleSignup = (email, password) => {
    const credentials = { email, password };
    localStorage.setItem('userCredentials', JSON.stringify(credentials)); // Save credentials to localStorage
    setUserCredentials(credentials);
    setIsAuthenticated(true); // Automatically log in the user after signup
  };

  const handleLogin = (email, password) => {
    if (userCredentials && userCredentials.email === email && userCredentials.password === password) {
      setIsAuthenticated(true);
    } else {
      alert("Invalid email or password");
    }
  };

  const handleCreateQuiz = (newQuiz) => {
    const updatedQuizzes = [...quizzes, newQuiz];
    setQuizzes(updatedQuizzes);
    // Save to localStorage to persist quizzes across sessions
    localStorage.setItem('quizzes', JSON.stringify(updatedQuizzes));
  };

  const getQuizDataById = (quizId) => {
    const savedQuizzes = JSON.parse(localStorage.getItem('quizzes')) || quizzes;
    return savedQuizzes.find(quiz => quiz.id === quizId);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            isAuthenticated ? <Navigate to="/home/dashboard" /> : <Auth onLogin={handleLogin} onSignup={handleSignup} />
          }
        />
        <Route
          path="/home/*"
          element={
            isAuthenticated ? <HomePage onCreateQuiz={handleCreateQuiz} /> : <Navigate to="/login" />
          }
        />
        <Route
          path="/quiz/:quizId"
          element={<QuizPage getQuizDataById={getQuizDataById} />} // Make QuizPage publicly accessible
        />
        <Route path="*" element={<Navigate to={isAuthenticated ? "/home/dashboard" : "/login"} />} />
      </Routes>
    </Router>
  );
}

export default App;
