import React, { useState } from 'react';
import { Link, Route, Routes, Navigate } from 'react-router-dom';
import Dashboard from './Dashboard';
import AnalyticsPage from './AnalyticsPage';
import './HomePage.css';
import Modal from './Modal';

const HomePage = () => {
  const [isModalOpen, setModalOpen] = useState(false);

  const handleOpenModal = () => {
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  };

  const handleContinue = (quizName, quizType) => {
    setModalOpen(false);
    console.log('Quiz Created:', quizName, quizType);
    // Add your quiz creation logic here
  };

  return (
    <div className="home-container">
      <aside className="sidebar">
        <div className="logo">QUIZZIE</div>
        <nav className="nav-menu">
          <ul>
            <li className="nav-item">
              <Link to="/home/dashboard">Dashboard</Link>
            </li>
            <li className="nav-item">
              <Link to="/home/analytics">Analytics</Link>
            </li>
            <li className="nav-item">
              <button className="create-quiz-button" onClick={handleOpenModal}>
                Create Quiz
              </button>
            </li>
          </ul>
        </nav>
        <div className="logout">LOGOUT</div>
      </aside>

      <main className={`main-content ${isModalOpen ? 'blur' : ''}`}>
        <Routes>
          <Route path="/" element={<Navigate to="/home/dashboard" />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/analytics" element={<AnalyticsPage />} />
          {/* Other routes can be added here */}
        </Routes>
      </main>

      <Modal isOpen={isModalOpen} onClose={handleCloseModal} onContinue={handleContinue} />
    </div>
  );
};

export default HomePage;
