import React, { useState } from 'react';
import PollQuestionModal from './PollQuestionModal';
import QAQuestionModal from './QAQuestionModal'; // Assuming you have this component for Q&A

const CreateQuiz = () => {
  const [quizType, setQuizType] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = (type) => {
    setQuizType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleCreatePoll = (pollData) => {
    console.log('Poll created with data:', pollData);
    closeModal();
    // Handle the poll data (e.g., save it, display it, etc.)
  };

  const handleCreateQuiz = (quizData) => {
    console.log('Quiz created with data:', quizData);
    closeModal();
    // Handle the quiz data (e.g., save it, display it, etc.)
  };

  return (
    <div>
      <button onClick={() => openModal('Poll')}>Create Poll</button>
      <button onClick={() => openModal('Q&A')}>Create Q&A Quiz</button>

      {quizType === 'Poll' && (
        <PollQuestionModal isOpen={isModalOpen} onClose={closeModal} onCreatePoll={handleCreatePoll} />
      )}
      {quizType === 'Q&A' && (
        <QAQuestionModal isOpen={isModalOpen} onClose={closeModal} onCreateQuiz={handleCreateQuiz} />
      )}
    </div>
  );
};

export default CreateQuiz;
