import React, { useEffect, useState } from 'react';
import './Dashboard.css';

const Dashboard = () => {
  const [trendingQuizzes, setTrendingQuizzes] = useState([]);

  useEffect(() => {
    // Fetch quizzes from local storage
    const storedQuizzes = JSON.parse(localStorage.getItem('quizzes')) || [];

    // Sort quizzes by views in descending order
    const sortedQuizzes = storedQuizzes.sort((a, b) => b.views - a.views);

    // Update state with sorted quizzes
    setTrendingQuizzes(sortedQuizzes);
  }, []);

  return (
    <div className="dashboard-container">
      <div className="stats-container">
        <div className="stat-item">
          <h2 className="stat-number orange">{trendingQuizzes.length}</h2>
          <p>Quizzes Created</p>
        </div>
        <div className="stat-item">
          <h2 className="stat-number green">
            {trendingQuizzes.reduce((sum, quiz) => sum + quiz.questions.length, 0)}
          </h2>
          <p>Questions Created</p>
        </div>
        <div className="stat-item">
          <h2 className="stat-number blue">
            {trendingQuizzes.reduce((sum, quiz) => sum + quiz.views, 0)}
          </h2>
          <p>Total Impressions</p>
        </div>
      </div>

      <section className="trending-quizzes">
        <h3>Trending Quizzes</h3>
        <div className="quizzes-grid">
          {trendingQuizzes.length === 0 ? (
            <p>No quizzes available</p>
          ) : (
            trendingQuizzes.map((quiz, index) => (
              <div className="quiz-card" key={index}>
                <h4>{quiz.quizName}</h4>
                <p className="quiz-stats">
                  <span className="views">üëÅÔ∏è {quiz.views}</span>
                  <span className="created-date">Created on: {new Date(quiz.createdAt).toLocaleDateString()}</span>
                </p>
              </div>
            ))
          )}
        </div>
      </section>
    </div>
  );
};

export default Dashboard;
