import React, { useState, useEffect } from 'react';
import './PollQuestionModal.css';

const PollQuestionModal = ({ isOpen, onClose, onCreatePoll }) => {
  const defaultQuestion = { questionText: '', options: [''] };
  const [questions, setQuestions] = useState([defaultQuestion]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [quizName, setQuizName] = useState('');

  useEffect(() => {
    if (isOpen) {
      // Reset state when the modal is opened
      setQuestions([defaultQuestion]);
      setCurrentQuestionIndex(0);
      setQuizName('');
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isOpen]); // Run this effect when isOpen changes

  const handleAddOption = () => {
    if (questions[currentQuestionIndex].options.length < 4) {
      const updatedQuestions = [...questions];
      updatedQuestions[currentQuestionIndex].options.push('');
      setQuestions(updatedQuestions);
    }
  };

  const handleOptionChange = (index, value) => {
    const updatedQuestions = [...questions];
    updatedQuestions[currentQuestionIndex].options[index] = value;
    setQuestions(updatedQuestions);
  };

  const handleRemoveOption = (index) => {
    const updatedQuestions = [...questions];
    updatedQuestions[currentQuestionIndex].options = updatedQuestions[currentQuestionIndex].options.filter(
      (_, i) => i !== index
    );
    setQuestions(updatedQuestions);
  };

  const handleQuestionTextChange = (value) => {
    const updatedQuestions = [...questions];
    updatedQuestions[currentQuestionIndex].questionText = value;
    setQuestions(updatedQuestions);
  };

  const handleAddQuestion = () => {
    if (questions.length < 5) {
      setQuestions([...questions, defaultQuestion]);
      setCurrentQuestionIndex(questions.length);
    }
  };

  const handleQuestionSelect = (index) => {
    setCurrentQuestionIndex(index);
  };

  const handleSubmit = () => {
    // Submit the poll and reset the state
    onCreatePoll({ quizName, questions });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content poll-question-modal">
        <div className="quiz-name">
          <input
            type="text"
            placeholder="Quiz Name"
            value={quizName}
            onChange={(e) => setQuizName(e.target.value)}
          />
        </div>
        <div className="question-nav">
          {questions.map((_, index) => (
            <div
              key={index}
              className={`question-num ${index === currentQuestionIndex ? 'active' : ''}`}
              onClick={() => handleQuestionSelect(index)}
            >
              {index + 1}
            </div>
          ))}
          {questions.length < 5 && (
            <div className="add-question" onClick={handleAddQuestion}>
              +
            </div>
          )}
        </div>
        <input
          type="text"
          placeholder="Poll Question"
          value={questions[currentQuestionIndex].questionText}
          onChange={(e) => handleQuestionTextChange(e.target.value)}
          className="poll-question-input"
        />
        <div className="options-container">
          {questions[currentQuestionIndex].options.map((option, index) => (
            <div key={index} className="option">
              <input
                type="text"
                value={option}
                onChange={(e) => handleOptionChange(index, e.target.value)}
                placeholder="Option Text"
                className="option-text-input"
              />
              {index > 0 && (
                <button className="remove-option" onClick={() => handleRemoveOption(index)}>
                  🗑️
                </button>
              )}
            </div>
          ))}
          {questions[currentQuestionIndex].options.length < 4 && (
            <button className="add-option" onClick={handleAddOption}>
              Add Option
            </button>
          )}
        </div>
        <div className="modal-buttons">
          <button className="modal-button cancel-button" onClick={onClose}>
            Cancel
          </button>
          <button
            className="modal-button continue-button"
            onClick={handleSubmit}
          >
            Create Poll
          </button>
        </div>
      </div>
    </div>
  );
};

export default PollQuestionModal;
